<?php require_once('../../../../wp-load.php'); global $wpdb; echo "Database prefix: {$wpdb->prefix}\n"; echo "Checking if tables exist:\n"; $tables = [ $wpdb->prefix . 'edc_school_teachers', $wpdb->prefix . 'edc_school_students' ]; foreach ($tables as $table) { $exists = $wpdb->get_var("SHOW TABLES LIKE '{$table}'"); echo $table . ": " . ($exists ? "Exists" : "Missing") . "\n"; if ($exists) { $count = $wpdb->get_var("SELECT COUNT(*) FROM {$table}"); echo "  - Records: {$count}\n"; } } ?>
